import React from "react";
import TestimonalApi from "./TestimonalApi";

const Slide = ({ id, image, design, name, officer, post, date, desc, valueIndex, index}) => {
    let position = "nextSlide"
    if(valueIndex === index){
        position = "activeSlide"
    }
    if(valueIndex === index-1 || (index === 0 && valueIndex === TestimonalApi.length - 1)) {
        position = "lastSlide"
    }
  return(
  <>
    <article className={'d_flex ${position}'}>
    {/* <article className="box d_flex"> */}
      <div className="left box_shadow">
        <div className="img">
          <img src={image} alt="" />
        </div>
        <div className="details mtop">
          <span>{design}</span>
          <h2>{name}</h2>
          <label>{officer}</label>
        </div>
      </div>

      <div className="right">
        <div className="icon">
          <div className="quote">
            <i className="fal fa-quote-right"></i>
          </div>
        </div>
        <div className="content box_shadow mtop">
            <h2>{post}</h2>
            <h3>{date}</h3>
            <p>{desc}</p>
        </div>
      </div>
    </article>
  </>
  )
};

export default Slide;
