import React, { useState, useEffect } from "react";
import TestimonalApi from "./TestimonalApi";
import Slide from "./Slide";
import "./Testimonal.scss"

const Testimonals = () => {

  const [date, setdata] = useState(TestimonalApi);
  const [index, setIndex] = useState(0);

  useEffect(() => {
    const lastIndex = date.length - 1
    if(index < 0) {
        setIndex(lastIndex)
    }
    if(index > lastIndex) {
        setIndex(0)
    }
  
    
  }, [index, date])

  useEffect(() => {
    let slider = setInterval(() => {
        setIndex(index + 1)
    })
  
    return () => clearInterval(slider)
  }, [index])
  
  
  return (
    <>
      <section className="Testimonal" id="clients">
        <div className="container">
          <div className="heading text-center">
            <h4>WHAT CLIENTS SAY</h4>
            <h2>Testimonals</h2>
          </div>
          <div className="slide">
            {date.map((value, valueIndex) => {
              return (
                <Slide
                  key={value.id}
                  {...value}
                  valueIndex={valueIndex}
                  //   index={index}
                />
              );
            })}

            <div className="slide_button">
              <button className="btn_shadow prev_btn" onClick={() => setIndex(index - 1)}>
              <i class="fa fa-arrow-left"></i>
                {/* <i className="fas fa-arrow-left"></i> */}
              </button>
              <button className="btn_shadow next_btn"  onClick={() => setIndex(index + 1)}>
                <i className="fas fa-arrow-right"></i>
              </button>
            </div>
          </div>
        </div>
      </section>
    </>
  );
};

export default Testimonals;
